def number_journalists(journalists)
  puts "La liste contient #{journalists.count} journalistes"
end

def handle_with_number(journalists)
  namewnumber = 0
  journalists.each do |name|
    name = [name].map {|char| char[/\d+/]}
    if !name[0].nil?
      namewnumber += 1
    end
  end
  puts "La liste contient #{namewnumber} pseudos avec au moins un chiffre"
end

def handle_with_aude(journalists)
  namewaude = 0
  journalists.each do |name|
    if name.include?("aude")
     namewaude += 1
    end 
  
  end
  puts "La liste contient #{namewaude} pseudos contenant le prénom Aude"

end

def handle_beggining_uppercase(journalists)
  capitalname = 0
  journalists.each do |name|
    firstletter = name[1]
    if firstletter.upcase == firstletter
     capitalname += 1
    end 
  end
  puts "#{capitalname} pseudos de cette liste commencent par une majuscule"
end

def handle_with_uppercase(journalists)
  uppercasename = 0
  journalists.each do |name|
    unless name.downcase == name
    uppercasename +=1
    end
  end
  puts "#{uppercasename} pseudos contiennent des majuscules"
end

def number_underscore(journalists)
  underscore = 0 
  journalists.each do |name|
    underscore += name.count("_")
  end
  puts "La liste contient #{underscore} underscores au total"
end

def alphabetical_order(journalists)
  puts journalists.sort
end

def shortest_handle(journalists)
  puts journalists.sort_by(&:length)[0...50]
end

def where_is_epenser(journalists)
  puts journalists.index("@epenser")
end

def menu(journalists)
  puts "Que voulez-vous savoir ? Tapez le numéro de la question qui vous intéresse"
  puts "2 : Combien de journalistes ?"
  puts "3 : Combien de pseudos avec un numéro ?"
  puts "4 : Combien de pseudos contenant Aude ?"
  puts "5 : Combien commencent par une majuscule ?"
  puts "6 :Combien contiennent une majuscule ?"
  puts "7 : Combien y a-t-il de underscore _ dans tous les pseudos confondus ?"
  puts "8 : Liste par ordre alphabétique"
  puts "9 : 50 pseudos les plus courts"
  puts "10 : Quelle est la position de @epenser dans la liste ?"
  puts "Pour sortir du programme tapez 0"
  
  while 1 == 1
  user_choice = gets.chomp.to_i
    puts user_choice
      
    if user_choice == 2
      number_journalists(journalists)
    end

    if user_choice == 3
      handle_with_number(journalists)
    end

    if user_choice == 4
      handle_with_aude(journalists)
    end

    if user_choice == 5
      handle_beggining_uppercase(journalists)
    end

    if user_choice == 6
      handle_with_uppercase(journalists)
    end

    if user_choice == 7
      number_underscore(journalists)
    end

    if user_choice == 8
      alphabetical_order(journalists)
    end

    if user_choice == 9
      shortest_handle(journalists)
    end
    if user_choice == 10
      where_is_epenser(journalists)
    end

    if user_choice == 0
      exit
    end
  end
end

menu(journalists)
